--
-- Table structure for table `wp_posts`
--

DROP TABLE IF EXISTS `wp_posts`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `wp_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext NOT NULL,
  `post_title` text NOT NULL,
  `post_excerpt` text NOT NULL,
  `post_status` varchar(20) NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) NOT NULL DEFAULT 'open',
  `post_password` varchar(20) NOT NULL DEFAULT '',
  `post_name` varchar(200) NOT NULL DEFAULT '',
  `to_ping` text NOT NULL,
  `pinged` text NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=MyISAM AUTO_INCREMENT=51 DEFAULT CHARSET=utf8;
/*!40101 SET character_set_client = @saved_cs_client */;

--
-- Dumping data for table `wp_posts`
--

LOCK TABLES `wp_posts` WRITE;
/*!40000 ALTER TABLE `wp_posts` DISABLE KEYS */;
INSERT INTO `wp_posts` VALUES (1,1,'2013-08-06 04:11:21','2013-08-06 04:11:21','Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!','Hello world!','','publish','open','open','','hello-world','','','2013-08-06 04:11:21','2013-08-06 04:11:21','',0,'http://wordpress.nlgshow.com/?p=1',0,'post','',1),(2,1,'2013-08-06 04:11:21','2013-08-06 04:11:21','This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://wordpress.nlgshow.com/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!','Sample Page','','trash','open','open','','sample-page','','','2013-08-06 12:46:28','2013-08-06 12:46:28','',0,'http://wordpress.nlgshow.com/?page_id=2',0,'page','',0),(3,1,'2013-08-06 04:11:35','0000-00-00 00:00:00','','Auto Draft','','auto-draft','open','open','','','','','2013-08-06 04:11:35','0000-00-00 00:00:00','',0,'http://wordpress.nlgshow.com/?p=3',0,'post','',0),(4,1,'2013-08-06 12:38:58','2013-08-06 12:38:58','','Home','','publish','open','open','','home','','','2013-08-06 12:39:04','2013-08-06 12:39:04','',0,'http://wordpress.nlgshow.com/?p=4',1,'nav_menu_item','',0),(5,1,'2013-08-06 12:38:58','2013-08-06 12:38:58',' ','','','publish','open','open','','5','','','2013-08-06 12:39:04','2013-08-06 12:39:04','',0,'http://wordpress.nlgshow.com/?p=5',2,'nav_menu_item','',0),(7,1,'2013-08-06 12:43:08','2013-08-06 12:43:08','','Overview','','publish','open','open','','overview','','','2013-08-06 15:51:54','2013-08-06 15:51:54','',0,'http://wordpress.nlgshow.com/?p=7',2,'nav_menu_item','',0),(8,1,'2013-08-06 12:43:08','2013-08-06 12:43:08','','Buyers','','publish','open','open','','buyers','','','2013-08-06 15:51:54','2013-08-06 15:51:54','',0,'http://wordpress.nlgshow.com/?p=8',14,'nav_menu_item','',0),(9,1,'2013-08-06 12:43:08','2013-08-06 12:43:08','','Vendors','','publish','open','open','','vendors','','','2013-08-06 15:51:54','2013-08-06 15:51:54','',0,'http://wordpress.nlgshow.com/?p=9',15,'nav_menu_item','',0),(10,1,'2013-08-06 12:43:08','2013-08-06 12:43:08','','Media','','publish','open','open','','media','','','2013-08-06 15:51:54','2013-08-06 15:51:54','',0,'http://wordpress.nlgshow.com/?p=10',16,'nav_menu_item','',0),(11,1,'2013-08-06 12:43:08','2013-08-06 12:43:08','','Register','','publish','open','open','','register','','','2013-08-06 15:51:54','2013-08-06 15:51:54','',0,'http://wordpress.nlgshow.com/?p=11',17,'nav_menu_item','',0),(12,1,'2013-08-06 12:43:08','2013-08-06 12:43:08','','Photo Gallery','','publish','open','open','','photo-gallery','','','2013-08-06 15:51:54','2013-08-06 15:51:54','',0,'http://wordpress.nlgshow.com/?p=12',18,'nav_menu_item','',0),(13,1,'2013-08-06 12:43:08','2013-08-06 12:43:08','','Contact','','publish','open','open','','contact','','','2013-08-06 15:51:54','2013-08-06 15:51:54','',0,'http://wordpress.nlgshow.com/?p=13',19,'nav_menu_item','',0),(14,1,'2013-08-06 12:45:41','2013-08-06 12:45:41','<div>Because this event is a human interaction event to serve people and not solely a money making organization we hope that God will grant you relationships that foster, grow and serve your needs in the business world.\r\n\r\nMay this event be prosperous for all persons that attend, and, that it effectively serves your needs in this industry.\r\n\r\nMay the business that brought you our way prosper and every appointment you have and every message you receive add to your joy.\r\n\r\nMay our staff serve you in a manner that pleases you and your experience with us is memorable.\r\n\r\nWhen you leave, may your journey home be safe and your business contacts profitable.\r\n\r\nWe are all travelers. From \'birth till death\' we travel between eternities. May the days you spent with us be pleasant for you, profitable for society, helpful for those you meet and a joy to those who know and love you best.</div>\r\n&nbsp;','NLGS Mission Statement','','publish','open','open','','nlgs-mission-statement','','','2013-08-06 12:45:41','2013-08-06 12:45:41','',0,'http://wordpress.nlgshow.com/?page_id=14',0,'page','',0),(15,1,'2013-08-06 12:45:41','2013-08-06 12:45:41','<div>Because this event is a human interaction event to serve people and not solely a money making organization we hope that God will grant you relationships that foster, grow and serve your needs in the business world.\r\n\r\nMay this event be prosperous for all persons that attend, and, that it effectively serves your needs in this industry.\r\n\r\nMay the business that brought you our way prosper and every appointment you have and every message you receive add to your joy.\r\n\r\nMay our staff serve you in a manner that pleases you and your experience with us is memorable.\r\n\r\nWhen you leave, may your journey home be safe and your business contacts profitable.\r\n\r\nWe are all travelers. From \'birth till death\' we travel between eternities. May the days you spent with us be pleasant for you, profitable for society, helpful for those you meet and a joy to those who know and love you best.</div>\r\n&nbsp;','NLGS Mission Statement','','inherit','open','open','','14-revision-v1','','','2013-08-06 12:45:41','2013-08-06 12:45:41','',14,'http://wordpress.nlgshow.com/?p=15',0,'revision','',0),(16,1,'2013-08-06 12:46:06','2013-08-06 12:46:06',' ','','','publish','open','open','','16','','','2013-08-06 15:51:54','2013-08-06 15:51:54','',0,'http://wordpress.nlgshow.com/?p=16',3,'nav_menu_item','',0),(17,1,'2013-08-06 12:46:28','2013-08-06 12:46:28','This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://wordpress.nlgshow.com/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!','Sample Page','','inherit','open','open','','2-revision-v1','','','2013-08-06 12:46:28','2013-08-06 12:46:28','',2,'http://wordpress.nlgshow.com/?p=17',0,'revision','',0),(18,1,'2013-08-06 14:56:16','2013-08-06 14:56:16','<div class=\"content\"><img alt=\"\" src=\"/sites/all/themes/NLGS2010/images/content/WhyAttend.png\" />\r\n<h3 style=\"text-align: center;\"><em>As a Buyer...</em></h3>\r\n<p style=\"text-align: center;\"><strong><span style=\"text-decoration: underline;\">This is Not Your Average Trade Show!</span></strong> The National Lawn &amp; Garden Show is not the trade show you\'re used to!</p>\r\n\r\n<ul>\r\n	<li><strong>You save time with our signature pre-set appointment format.</strong></li>\r\n	<li><strong>You select the products you want to see, eliminating unnecessary appointments.</strong></li>\r\n	<li><strong>Vendors bring the products to you at your private conference table.</strong></li>\r\n	<li><strong>Presentations will be personalized with your requirements. </strong></li>\r\n	<li><strong>Enjoy networking opportunities, events, product displays and new product prototype reviews, too!</strong></li>\r\n</ul>\r\n<a href=\"/testimonials\">Click to see TESTIMONIALS</a>\r\n\r\nView our <a href=\"http://www.nlgshow.com/gallery\">Appointments photo gallery!</a>\r\n<h3 style=\"text-align: center;\"><em>As a vendor...</em></h3>\r\n<p style=\"text-align: center;\"><strong><span style=\"text-decoration: underline;\">An Event with Guaranteed Results!</span></strong>\r\nThe National Lawn &amp; Garden Show is best described as \"speed-dating for Lawn and Garden products!\"</p>\r\n\r\n<ul>\r\n	<li>There\'s <strong><span style=\"text-decoration: underline;\">no booth</span></strong> and <strong><span style=\"text-decoration: underline;\">no additional set up expenses.</span></strong></li>\r\n	<li>Here, <strong>you are in the drivers seat</strong>. Instead of standing in a booth, you move from buyer to buyer according to a guaranteed, pre-set appointment schedule.</li>\r\n	<li>No more waiting and wondering if you\'ll see the right buyer. As a Vendor at the National Lawn &amp; Garden Show, you\'ll meet one on one with real decision makers who will give you a yes or a no on the spot! <strong><span style=\"text-decoration: underline;\"><a href=\"/satisfactionguarantee\">Your satisfaction is guaranteed.</a></span></strong></li>\r\n	<li>We are so confident in the appointment format, that we guarantee you will meet buyers on a pre-set, one-on-one, appointment basis. <strong>No other trade event in the industry can offer you this!</strong></li>\r\n</ul>\r\n<a href=\"/testimonials\">Click to see TESTIMONIALS</a>\r\n\r\nView our <a href=\"http://www.nlgshow.com/gallery\">Appointments photo gallery!</a>\r\n\r\n</div>','Why Attend the National Lawn & Garden Show?','','publish','open','open','','why-attend-the-national-lawn-garden-show','','','2013-08-06 14:56:16','2013-08-06 14:56:16','',0,'http://wordpress.nlgshow.com/?page_id=18',0,'page','',0),(19,1,'2013-08-06 14:56:16','2013-08-06 14:56:16','<div class=\"content\"><img alt=\"\" src=\"/sites/all/themes/NLGS2010/images/content/WhyAttend.png\" />\r\n<h3 style=\"text-align: center;\"><em>As a Buyer...</em></h3>\r\n<p style=\"text-align: center;\"><strong><span style=\"text-decoration: underline;\">This is Not Your Average Trade Show!</span></strong> The National Lawn &amp; Garden Show is not the trade show you\'re used to!</p>\r\n\r\n<ul>\r\n	<li><strong>You save time with our signature pre-set appointment format.</strong></li>\r\n	<li><strong>You select the products you want to see, eliminating unnecessary appointments.</strong></li>\r\n	<li><strong>Vendors bring the products to you at your private conference table.</strong></li>\r\n	<li><strong>Presentations will be personalized with your requirements. </strong></li>\r\n	<li><strong>Enjoy networking opportunities, events, product displays and new product prototype reviews, too!</strong></li>\r\n</ul>\r\n<a href=\"/testimonials\">Click to see TESTIMONIALS</a>\r\n\r\nView our <a href=\"http://www.nlgshow.com/gallery\">Appointments photo gallery!</a>\r\n<h3 style=\"text-align: center;\"><em>As a vendor...</em></h3>\r\n<p style=\"text-align: center;\"><strong><span style=\"text-decoration: underline;\">An Event with Guaranteed Results!</span></strong>\r\nThe National Lawn &amp; Garden Show is best described as \"speed-dating for Lawn and Garden products!\"</p>\r\n\r\n<ul>\r\n	<li>There\'s <strong><span style=\"text-decoration: underline;\">no booth</span></strong> and <strong><span style=\"text-decoration: underline;\">no additional set up expenses.</span></strong></li>\r\n	<li>Here, <strong>you are in the drivers seat</strong>. Instead of standing in a booth, you move from buyer to buyer according to a guaranteed, pre-set appointment schedule.</li>\r\n	<li>No more waiting and wondering if you\'ll see the right buyer. As a Vendor at the National Lawn &amp; Garden Show, you\'ll meet one on one with real decision makers who will give you a yes or a no on the spot! <strong><span style=\"text-decoration: underline;\"><a href=\"/satisfactionguarantee\">Your satisfaction is guaranteed.</a></span></strong></li>\r\n	<li>We are so confident in the appointment format, that we guarantee you will meet buyers on a pre-set, one-on-one, appointment basis. <strong>No other trade event in the industry can offer you this!</strong></li>\r\n</ul>\r\n<a href=\"/testimonials\">Click to see TESTIMONIALS</a>\r\n\r\nView our <a href=\"http://www.nlgshow.com/gallery\">Appointments photo gallery!</a>\r\n\r\n</div>','Why Attend the National Lawn & Garden Show?','','inherit','open','open','','18-revision-v1','','','2013-08-06 14:56:16','2013-08-06 14:56:16','',18,'http://wordpress.nlgshow.com/?p=19',0,'revision','',0),(20,1,'2013-08-06 14:57:16','2013-08-06 14:57:16',' ','','','publish','open','open','','20','','','2013-08-06 15:51:54','2013-08-06 15:51:54','',0,'http://wordpress.nlgshow.com/?p=20',4,'nav_menu_item','',0),(21,1,'2013-08-06 14:58:50','2013-08-06 14:58:50','<div class=\"content\">\r\n    <img src=\"/sites/all/themes/NLGS2010/images/content/ApptFormat.png\">\r\n\r\n<p>Guaranteed Pre-Set, Scheduled Appointments are the specialty of the National Lawn & Garden Show and are what sets the event apart from other trade events. High volume buyers are matched with quality vendors based on common product interests resulting in thousands of qualified appointments. Ample time is reserved for buyers to visit the Point of Purchase (POP) Showcase and New Product/Prototype areas.</p>\r\n\r\n<h3>\r\nWho participates?\r\n</h3>\r\n\r\n<p>\r\nNLGS Appointments are exclusive and open only to qualified decision-making buyers and vendors. Vendors must have high volume manufacturing capabilities, possess the ability to distribute nationally, and be prepared to entertain international interest. Buyers are high volume buyers with extensive purchasing authority and each buying company must meet minimum sales volume or store count to qualify and attend.\r\n</p>\r\n\r\n<table>\r\n<tr>\r\n<td style=\"width:270px;\">- Buying Groups</td>\r\n<td style=\"width:250px;\">- Catalogs and Direct Mail Companies</td>\r\n</tr>\r\n\r\n<tr>\r\n<td>- Co-ops</td>\r\n<td>- Department Stores</td>\r\n</tr>\r\n\r\n<tr>\r\n<td>- E-commerce</td>\r\n<td>- Farm & Fleet Stores</td>\r\n</tr>\r\n\r\n<tr>\r\n<td>- Wholesale Distributors</td>\r\n<td>- Liquidators</td>\r\n</tr>\r\n\r\n<tr>\r\n<td>- Regional Retail Chains</td>\r\n<td>- Television and Home Shopping</td>\r\n</tr>\r\n\r\n<tr>\r\n<td>- Multi-unit Independent Garden Centers</td><td></td>\r\n</tr>\r\n</table>\r\n\r\n<H3>\r\nHow do the appointments work?\r\n</h3>\r\n\r\n<ul>\r\n<li>Buyers select the product categories they are interested in, which determines the vendors they will see.</li>\r\n\r\n<li>Vendors select their product categories, which determines which buyers they match. Closer to the event vendors\r\nchoose which \"matching\" buyers they would like to see and can sort the buyers by importance and priority.</li>\r\n\r\n<li>Appointments are scheduled in advance by proprietary automated appointment software. This means buyers and\r\nsellers will only see companies that match with their respective product categories.</li>\r\n\r\n<li>Appointment schedules are distributed to buyers and sellers at the event.</li>\r\n\r\n<li>Buyers are assigned conference tables while vendors follow their pre-set schedule and move from table to table.</li>\r\n\r\n<li>Each appointment is 10 minutes in length and automated voice prompts keep the appointments running smoothly.</li> \r\n\r\n<li>If a buyer and a  vendor wish to meet longer, \"Bonus Time\" is set aside each day to facilitate a second (or third) meeting. </li>\r\n\r\n<li>NLGS\'s software powered by Controlled Marketing Conferences, Inc.</li>\r\n</ul>  </div>\r\n\r\n  </div>		','Pre-Set Scheduled Appointments:','','publish','open','open','','pre-set-scheduled-appointments','','','2013-08-06 14:58:50','2013-08-06 14:58:50','',0,'http://wordpress.nlgshow.com/?page_id=21',0,'page','',0),(22,1,'2013-08-06 14:58:50','2013-08-06 14:58:50','<div class=\"content\">\r\n    <img src=\"/sites/all/themes/NLGS2010/images/content/ApptFormat.png\">\r\n\r\n<p>Guaranteed Pre-Set, Scheduled Appointments are the specialty of the National Lawn & Garden Show and are what sets the event apart from other trade events. High volume buyers are matched with quality vendors based on common product interests resulting in thousands of qualified appointments. Ample time is reserved for buyers to visit the Point of Purchase (POP) Showcase and New Product/Prototype areas.</p>\r\n\r\n<h3>\r\nWho participates?\r\n</h3>\r\n\r\n<p>\r\nNLGS Appointments are exclusive and open only to qualified decision-making buyers and vendors. Vendors must have high volume manufacturing capabilities, possess the ability to distribute nationally, and be prepared to entertain international interest. Buyers are high volume buyers with extensive purchasing authority and each buying company must meet minimum sales volume or store count to qualify and attend.\r\n</p>\r\n\r\n<table>\r\n<tr>\r\n<td style=\"width:270px;\">- Buying Groups</td>\r\n<td style=\"width:250px;\">- Catalogs and Direct Mail Companies</td>\r\n</tr>\r\n\r\n<tr>\r\n<td>- Co-ops</td>\r\n<td>- Department Stores</td>\r\n</tr>\r\n\r\n<tr>\r\n<td>- E-commerce</td>\r\n<td>- Farm & Fleet Stores</td>\r\n</tr>\r\n\r\n<tr>\r\n<td>- Wholesale Distributors</td>\r\n<td>- Liquidators</td>\r\n</tr>\r\n\r\n<tr>\r\n<td>- Regional Retail Chains</td>\r\n<td>- Television and Home Shopping</td>\r\n</tr>\r\n\r\n<tr>\r\n<td>- Multi-unit Independent Garden Centers</td><td></td>\r\n</tr>\r\n</table>\r\n\r\n<H3>\r\nHow do the appointments work?\r\n</h3>\r\n\r\n<ul>\r\n<li>Buyers select the product categories they are interested in, which determines the vendors they will see.</li>\r\n\r\n<li>Vendors select their product categories, which determines which buyers they match. Closer to the event vendors\r\nchoose which \"matching\" buyers they would like to see and can sort the buyers by importance and priority.</li>\r\n\r\n<li>Appointments are scheduled in advance by proprietary automated appointment software. This means buyers and\r\nsellers will only see companies that match with their respective product categories.</li>\r\n\r\n<li>Appointment schedules are distributed to buyers and sellers at the event.</li>\r\n\r\n<li>Buyers are assigned conference tables while vendors follow their pre-set schedule and move from table to table.</li>\r\n\r\n<li>Each appointment is 10 minutes in length and automated voice prompts keep the appointments running smoothly.</li> \r\n\r\n<li>If a buyer and a  vendor wish to meet longer, \"Bonus Time\" is set aside each day to facilitate a second (or third) meeting. </li>\r\n\r\n<li>NLGS\'s software powered by Controlled Marketing Conferences, Inc.</li>\r\n</ul>  </div>\r\n\r\n  </div>		','Pre-Set Scheduled Appointments:','','inherit','open','open','','21-revision-v1','','','2013-08-06 14:58:50','2013-08-06 14:58:50','',21,'http://wordpress.nlgshow.com/?p=22',0,'revision','',0),(23,1,'2013-08-06 14:59:09','2013-08-06 14:59:09',' ','','','publish','open','open','','23','','','2013-08-06 15:51:54','2013-08-06 15:51:54','',0,'http://wordpress.nlgshow.com/?p=23',5,'nav_menu_item','',0),(24,1,'2013-08-06 15:02:38','2013-08-06 15:02:38','<div class=\"content\">\r\n    <img src=\"/sites/all/themes/NLGS2010/images/content/O_pop.png\">\r\n\r\n<p>The Point of Purchase \"POP\" Showcase gives vendors the chance to display products, packaging and POP material in a retail setting. No\r\ntraditional tradeshow backdrops are allowed. No utilities are available in this area. Buyers spend time in the Point of Purchase Showcase\r\nin preparation for appointments. Buyers have continuous access to view products in this area throughout the event.</p>\r\n\r\n<h3>\r\nHow are products displayed in the Point of Purchase Showcase?\r\n</h3>\r\n\r\n<ul>\r\n<li><strong>Point of Purchase Showcase:</strong> Up to three 2\' x 2\' POP spaces can be reserved for each vendor. Floor or table top space is\r\navailable.</li>\r\n</ul>\r\n\r\n<h3>\r\nHow much does A Point of Purchase Display cost?\r\n</h3>\r\n\r\n<ul>\r\n<li>\r\nThree 2\' x 2\' spaces are included with vendor registration. Additional POP space can be purchased for $100 per additional 2\'x2\'\r\nspace.\r\n</li>\r\n</ul>\r\n\r\n<p>View our <a href=\"http://www.nlgshow.com/galleryEvents\">POP photo gallery!</a></p>  </div>\r\n\r\n  </div>','Point of Purchase Showcase (POP)','','publish','open','open','','point-of-purchase-showcase-pop','','','2013-08-06 15:02:38','2013-08-06 15:02:38','',0,'http://wordpress.nlgshow.com/?page_id=24',0,'page','',0),(25,1,'2013-08-06 15:02:38','2013-08-06 15:02:38','<div class=\"content\">\r\n    <img src=\"/sites/all/themes/NLGS2010/images/content/O_pop.png\">\r\n\r\n<p>The Point of Purchase \"POP\" Showcase gives vendors the chance to display products, packaging and POP material in a retail setting. No\r\ntraditional tradeshow backdrops are allowed. No utilities are available in this area. Buyers spend time in the Point of Purchase Showcase\r\nin preparation for appointments. Buyers have continuous access to view products in this area throughout the event.</p>\r\n\r\n<h3>\r\nHow are products displayed in the Point of Purchase Showcase?\r\n</h3>\r\n\r\n<ul>\r\n<li><strong>Point of Purchase Showcase:</strong> Up to three 2\' x 2\' POP spaces can be reserved for each vendor. Floor or table top space is\r\navailable.</li>\r\n</ul>\r\n\r\n<h3>\r\nHow much does A Point of Purchase Display cost?\r\n</h3>\r\n\r\n<ul>\r\n<li>\r\nThree 2\' x 2\' spaces are included with vendor registration. Additional POP space can be purchased for $100 per additional 2\'x2\'\r\nspace.\r\n</li>\r\n</ul>\r\n\r\n<p>View our <a href=\"http://www.nlgshow.com/galleryEvents\">POP photo gallery!</a></p>  </div>\r\n\r\n  </div>','Point of Purchase Showcase (POP)','','inherit','open','open','','24-revision-v1','','','2013-08-06 15:02:38','2013-08-06 15:02:38','',24,'http://wordpress.nlgshow.com/?p=25',0,'revision','',0),(26,1,'2013-08-06 15:03:58','2013-08-06 15:03:58',' <div class=\"content\">\r\n    <img src=\"/sites/all/themes/NLGS2010/images/content/prototype.png\">\r\n\r\n\r\n<p style=\"text-align:center;\"><em>Enjoy the rare opportunity to have buyers preview and evaluate your newest products  in the event\'s secure Prototype Room.</em></p>\r\n\r\n<p style=\"text-align:center;\">Here buyers share their opinions and written comments with you according to uniqueness, pricing, packaging and many other factors. This provides an invaluable source of information that helps shape the future design and production of lawn & garden products. Buyers also select a product for \"Best of Show Award\". Vendors will receive the buyer\'s written feedback at the end of the event. The area is accessible throughout the event for buyers only. The set-up is identical to the Point of Purchase (POP) area. </p> \r\n\r\n<h3>How much does it cost to display in the New Product Prototype Preview?</h3>\r\n\r\n<ul>\r\n<li>1 Product Entry: $250</li>\r\n<li>2 Product Entries: $350</li>\r\n<li>3 Product Entries $450</li> \r\n</ul>  </div>\r\n\r\n  </div>	','New Product/Prototype Review','','publish','open','open','','new-productprototype-review','','','2013-08-06 15:03:58','2013-08-06 15:03:58','',0,'http://wordpress.nlgshow.com/?page_id=26',0,'page','',0),(27,1,'2013-08-06 15:03:58','2013-08-06 15:03:58',' <div class=\"content\">\r\n    <img src=\"/sites/all/themes/NLGS2010/images/content/prototype.png\">\r\n\r\n\r\n<p style=\"text-align:center;\"><em>Enjoy the rare opportunity to have buyers preview and evaluate your newest products  in the event\'s secure Prototype Room.</em></p>\r\n\r\n<p style=\"text-align:center;\">Here buyers share their opinions and written comments with you according to uniqueness, pricing, packaging and many other factors. This provides an invaluable source of information that helps shape the future design and production of lawn & garden products. Buyers also select a product for \"Best of Show Award\". Vendors will receive the buyer\'s written feedback at the end of the event. The area is accessible throughout the event for buyers only. The set-up is identical to the Point of Purchase (POP) area. </p> \r\n\r\n<h3>How much does it cost to display in the New Product Prototype Preview?</h3>\r\n\r\n<ul>\r\n<li>1 Product Entry: $250</li>\r\n<li>2 Product Entries: $350</li>\r\n<li>3 Product Entries $450</li> \r\n</ul>  </div>\r\n\r\n  </div>	','New Product/Prototype Review','','inherit','open','open','','26-revision-v1','','','2013-08-06 15:03:58','2013-08-06 15:03:58','',26,'http://wordpress.nlgshow.com/?p=27',0,'revision','',0),(28,1,'2013-08-06 15:04:59','2013-08-06 15:04:59','<img src=\"/sites/all/themes/NLGS2010/images/content/testimonials.png\">\r\n\r\n\r\n<h3>What are previous <em>VENDORS</em> saying about NLGS?</h3>\r\n\r\n\r\n\r\n\r\n<p><em>\"One of the shows you should really attend, new customers as always.\"</em><br /><strong> Contech Enterprises, Inc.</strong></p>\r\n\r\n<p><em>\"I always suggest this show to both vendors and buyers.\"</em><br /><strong>\r\n  Earthbox</strong></p>\r\n\r\n<p><em>\"A must attend event--- not only to meet key buyers, but it is cost effective, you do not have to travel all around.\"</em><br /><strong>  Kooltron, Inc.</strong></p>\r\n\r\n<p><em>\"Qualified buyers in one location, at one hotel and one airfare.\"</em><br /><strong> Sterling International, Inc.</strong></p>\r\n\r\n<p><em>\"Met new buyers we would not have met otherwise, professional and casual, great if you are new to the business.\" </em><br /><strong> Rubber Design by Rainbow</strong></p>\r\n\r\n<p><em>\"Much more efficient and a better use of time than other events.\"</em><br /><strong> Generation Online</strong></p>\r\n\r\n<p><em>\"Face to face contact with buyers, 10 minutes is just enough time.\"</em><br /><strong> Jagram S. A.</strong></p>\r\n\r\n<p><em>\"Speed dating, effective, efficient. More efficient use of time and better ROI\" </em><br /><strong> Woodstock Chimes</strong></p>\r\n\r\n<p><em>\"Definitely a must attend-very productive show. Easy access to a wide variety of buyers/companies.\" </em><br /><strong> Zenith Innovation, LLC</strong></p>\r\n\r\n\r\n<h3>What are previous <em>BUYERS</em> saying about NLGS?</h3>\r\n\r\n\r\n\r\n<p><strong><em>SKH stated,</em></strong> \"This is an easy way to find \'Diamonds in the Rough\' and to be on the cutting edge of this industry\"</p>\r\n\r\n<p><strong><em>Armstrong Garden Center</em></strong> commented that this is an \"excellent format, makes the most of initial time with little waste.\" </p>\r\n\r\n<p><strong><em>Aco Hardware</em></strong> has said, \"A great way to meet a lot of vendors in a short period of time.\"</p>\r\n\r\n<p><strong><em>Commerce Distributors</em></strong> said, \"Amazing what can be accomplished in 10 minutes.\"</p>\r\n\r\n<p>Excellent, unmatched efficient use of time! Second to none!!\" said <em><strong>Meyer Seed of Baltimore</strong></em></p>\r\n\r\n<p><em><strong>The Greatest Gift,</em></strong> stated \"Saw many new products without walking a huge show.\"</p>\r\n\r\n<p><em><strong>Plow & Hearth</em></strong> \"Much more interesting and focused, I don\'t feel that I missed finding a good product\"</p>\r\n\r\n\r\n<p><em><strong>Earl May</em></strong> said, \"A show focused more on making contacts than walking booths\" and \"I was able to help a vendor with packaging ideas that eventually helped me sell more of their product\"</p>\r\n\r\n\r\n<p>\"One of a kind---A place to be and a place to be seen!\" <em><strong> Groupex</strong></em></p>\r\n\r\n\r\n<p><strong><em>L&L Nursery Supply, Inc.</em></strong> said, \"Better than most, more productive than the Hardware Show!\"</p>\r\n\r\n<p>\"Good mix of products, I talked to more vendors in 3 days than would be possible\r\nat other shows,\" said <strong><em>Charley\'s Greenhouse Supply</strong></em></p>\r\n\r\n\r\n<p><strong><em>Doctors Foster & Smith</em></strong> said, \" This event has been my premier event for finding products...a must attend event.\"</p>  </div>\r\n','NLGS Testimonials','','publish','open','open','','nlgs-testimonials','','','2013-08-06 15:04:59','2013-08-06 15:04:59','',0,'http://wordpress.nlgshow.com/?page_id=28',0,'page','',0),(29,1,'2013-08-06 15:04:59','2013-08-06 15:04:59','<img src=\"/sites/all/themes/NLGS2010/images/content/testimonials.png\">\r\n\r\n\r\n<h3>What are previous <em>VENDORS</em> saying about NLGS?</h3>\r\n\r\n\r\n\r\n\r\n<p><em>\"One of the shows you should really attend, new customers as always.\"</em><br /><strong> Contech Enterprises, Inc.</strong></p>\r\n\r\n<p><em>\"I always suggest this show to both vendors and buyers.\"</em><br /><strong>\r\n  Earthbox</strong></p>\r\n\r\n<p><em>\"A must attend event--- not only to meet key buyers, but it is cost effective, you do not have to travel all around.\"</em><br /><strong>  Kooltron, Inc.</strong></p>\r\n\r\n<p><em>\"Qualified buyers in one location, at one hotel and one airfare.\"</em><br /><strong> Sterling International, Inc.</strong></p>\r\n\r\n<p><em>\"Met new buyers we would not have met otherwise, professional and casual, great if you are new to the business.\" </em><br /><strong> Rubber Design by Rainbow</strong></p>\r\n\r\n<p><em>\"Much more efficient and a better use of time than other events.\"</em><br /><strong> Generation Online</strong></p>\r\n\r\n<p><em>\"Face to face contact with buyers, 10 minutes is just enough time.\"</em><br /><strong> Jagram S. A.</strong></p>\r\n\r\n<p><em>\"Speed dating, effective, efficient. More efficient use of time and better ROI\" </em><br /><strong> Woodstock Chimes</strong></p>\r\n\r\n<p><em>\"Definitely a must attend-very productive show. Easy access to a wide variety of buyers/companies.\" </em><br /><strong> Zenith Innovation, LLC</strong></p>\r\n\r\n\r\n<h3>What are previous <em>BUYERS</em> saying about NLGS?</h3>\r\n\r\n\r\n\r\n<p><strong><em>SKH stated,</em></strong> \"This is an easy way to find \'Diamonds in the Rough\' and to be on the cutting edge of this industry\"</p>\r\n\r\n<p><strong><em>Armstrong Garden Center</em></strong> commented that this is an \"excellent format, makes the most of initial time with little waste.\" </p>\r\n\r\n<p><strong><em>Aco Hardware</em></strong> has said, \"A great way to meet a lot of vendors in a short period of time.\"</p>\r\n\r\n<p><strong><em>Commerce Distributors</em></strong> said, \"Amazing what can be accomplished in 10 minutes.\"</p>\r\n\r\n<p>Excellent, unmatched efficient use of time! Second to none!!\" said <em><strong>Meyer Seed of Baltimore</strong></em></p>\r\n\r\n<p><em><strong>The Greatest Gift,</em></strong> stated \"Saw many new products without walking a huge show.\"</p>\r\n\r\n<p><em><strong>Plow & Hearth</em></strong> \"Much more interesting and focused, I don\'t feel that I missed finding a good product\"</p>\r\n\r\n\r\n<p><em><strong>Earl May</em></strong> said, \"A show focused more on making contacts than walking booths\" and \"I was able to help a vendor with packaging ideas that eventually helped me sell more of their product\"</p>\r\n\r\n\r\n<p>\"One of a kind---A place to be and a place to be seen!\" <em><strong> Groupex</strong></em></p>\r\n\r\n\r\n<p><strong><em>L&L Nursery Supply, Inc.</em></strong> said, \"Better than most, more productive than the Hardware Show!\"</p>\r\n\r\n<p>\"Good mix of products, I talked to more vendors in 3 days than would be possible\r\nat other shows,\" said <strong><em>Charley\'s Greenhouse Supply</strong></em></p>\r\n\r\n\r\n<p><strong><em>Doctors Foster & Smith</em></strong> said, \" This event has been my premier event for finding products...a must attend event.\"</p>  </div>\r\n','NLGS Testimonials','','inherit','open','open','','28-revision-v1','','','2013-08-06 15:04:59','2013-08-06 15:04:59','',28,'http://wordpress.nlgshow.com/?p=29',0,'revision','',0),(30,1,'2013-08-06 15:06:00','2013-08-06 15:06:00',' ','','','publish','open','open','','30','','','2013-08-06 15:51:54','2013-08-06 15:51:54','',0,'http://wordpress.nlgshow.com/?p=30',8,'nav_menu_item','',0),(31,1,'2013-08-06 15:06:00','2013-08-06 15:06:00',' ','','','publish','open','open','','31','','','2013-08-06 15:51:54','2013-08-06 15:51:54','',0,'http://wordpress.nlgshow.com/?p=31',7,'nav_menu_item','',0),(32,1,'2013-08-06 15:06:00','2013-08-06 15:06:00',' ','','','publish','open','open','','32','','','2013-08-06 15:51:54','2013-08-06 15:51:54','',0,'http://wordpress.nlgshow.com/?p=32',6,'nav_menu_item','',0),(33,1,'2013-08-06 15:33:32','2013-08-06 15:33:32','<img src=\"/sites/all/themes/NLGS2010/images/content/PrevAttend.png\">\r\n\r\n<p><strong>What type of Buyers Attend the National Lawn &amp; Garden Show?</strong></p>\r\n<p>NLGS is a B2B international event open to professional merchandisers in the lawn &amp; garden industry. NLGS is not a consumer show and is not open to the public.</p>\r\n<p><strong>Type of Buyers</strong></p>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td width=\"330\">\r\n<ul>\r\n<li>Regional Retail Chains</li>\r\n<li>Department Stores</li>\r\n<li>Wholesale Distributors</li>\r\n<li>Buying Groups/Coops</li>\r\n<li>Catalogue and Direct Mail</li>\r\n<li>E-Commerce</li>\r\n<li>Internet Web Sites</li>\r\n<li>Multi-Unit Independent Garden Centers</li>\r\n<li>Farm &amp; Fleet Stores</li>\r\n<li>Liquidators</li></ul></td>\r\n<td valign=\"top\" height=\"215\">\r\n<div style=\"BORDER-RIGHT: #004444 1px solid; PADDING-RIGHT: 10px; BORDER-TOP: #004444 1px solid; PADDING-LEFT: 10px; FLOAT: right; PADDING-BOTTOM: 10px; MARGIN: 0px 0px 5px 10px; BORDER-LEFT: #004444 1px solid; WIDTH: 180px; PADDING-TOP: 10px; BORDER-BOTTOM: #004444 1px solid\"><strong>Previous Buyer Attendees</strong><br /><a href=\"buyerslist\">Buyer Attendees</a></div></td></tr></tbody></table>\r\n\r\n<p><strong>What type of vendors attend the National Lawn &amp; Garden Show?</strong></p>\r\n<p>The majority of NLGS vendors are Lawn &amp; Garden, Water Garden and Pet manufacturers from the USA and Canada. Vendors from other countries include Asia, Australia, Europe and Mexico frequent the event.</p>\r\n<p><strong>Type of Vendors</strong></p>\r\n<table><tbody><tr>\r\n<td width=\"330\">\r\n<ul>\r\n<li>Manufacturers of Lawn &amp; Garden hardline products</li>\r\n<li>Importers of Lawn &amp; Garden hardline products</li>\r\n</ul>\r\n<p><strong>What type of Organizations may attend the National Lawn & Garden Show?</strong></p>\r\n<ul>\r\n<li>Wholesale Distributors</li>\r\n<li>Media and Advertising Agencies</li>\r\n<li>Industry Support Services</li>\r\n<li>Management Consultants</li>\r\n</ul>\r\n</td>\r\n<td valign=\"top\" height=\"150\">\r\n<div style=\"BORDER-RIGHT: #004444 1px solid; PADDING-RIGHT: 10px; BORDER-TOP: #004444 1px solid; PADDING-LEFT: 10px; FLOAT: right; PADDING-BOTTOM: 10px; MARGIN: 0px 0px 5px 10px; BORDER-LEFT: #004444 1px solid; WIDTH: 180px; PADDING-TOP: 10px; BORDER-BOTTOM: #004444 1px solid\"><strong>Previous Vendor Attendees<br /></strong><a href=\"previousvendors\">Vendor Attendees</a><br />\r\n</div></td></tr></tbody></table><br /><br /><br /><br /></TD></TR></TD></TR></TBODY></TABLE>','Previous Attendees','','publish','open','open','','previous-attendees','','','2013-08-06 15:33:32','2013-08-06 15:33:32','',0,'http://wordpress.nlgshow.com/?page_id=33',0,'page','',0),(34,1,'2013-08-06 15:33:32','2013-08-06 15:33:32','<img src=\"/sites/all/themes/NLGS2010/images/content/PrevAttend.png\">\r\n\r\n<p><strong>What type of Buyers Attend the National Lawn &amp; Garden Show?</strong></p>\r\n<p>NLGS is a B2B intern